import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Pesanan = (props) => {
  const [data,setData] = useState([]);
  const [error,setEror] = useState("");

  const [dariID, setDariID] = useState("");
  const [keID, setKeID] = useState("");
  // const [tgl, setTgl] = useState("");
  const [penumpang, setPenumpang] = useState(1);

  const formatTodayDate = () => {
    const today = new Date();
    const year = today.getFullYear();
    const month = String(today.getMonth() + 1).padStart(2, '0'); // Menghindari masalah dengan indeks bulan yang dimulai dari 0
    const day = String(today.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };
  
  const [tgl, setTgl] = useState(formatTodayDate());

  const handleDateChange = (e) => {
    const selectedDate = e.target.value;

    if (selectedDate >= formatTodayDate()) {
      // Jika tanggal yang dipilih lebih besar atau sama dengan tanggal hari ini
      setTgl(selectedDate);
    } else {
      // Jika tanggal yang dipilih kurang dari tanggal hari ini, kembalikan ke tanggal hari ini
      setTgl(formatTodayDate());
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/bandara");
        setData(response.data.data);
      } catch (error) {
        if (error.response && error.response.status === 429) {
          console.log("Rate Limit Exceeded");
        } else {
          console.error("An error occurred:", error);
        }
      }
    };

    fetchData();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (dariID === keID) {
      setEror("Bandara keberangkatan dan Bandara tujuan tidak boleh sama!");
      return;
    }
    localStorage.setItem('dariID', dariID);
    localStorage.setItem('keID', keID);
    localStorage.setItem('penumpang', penumpang);
    localStorage.setItem('tgl', tgl);

    console.log(dariID, keID, penumpang, tgl)
    window.location.href = "/userpage/caribandara";
  };

  const [passengers, setPassengers] = useState({
    dewasa: 4,
    anak: 3,
    bayi: 4,
  });

  const handlePassengerChange = (type, value) => {
    setPassengers({ ...passengers, [type]: value });
  };
  return (
      <div>
        <div className="content-wrapper">
          {/* Content Header (Page header) */}
          <div className="content-header">
            <div className="container-fluid">
              <h1 className="m-0">Pesanan Tiket</h1>
            </div>
          </div>

          <section className="content mt-3">
            <div className="container-fluid">
              <div className="card card-primary">
                <div className="card-header">
                  <h3 className="card-title">Cari tiket</h3>
                </div>
                <div className="card-body">
                  <div className="card-body">
                    <div className="row">
                      <div className="col-md-6">
                        <div className="form-group">
                          <label>Bandara Keberangkatan</label>
                          <select
                            className="form-control select2bs4"
                            style={{ width: "100%" }}
                            value={dariID}
                            onChange={(e) => setDariID(e.target.value)}
                            required
                          >
                            {data.map((item, index) => (
                            <option key={index} value={item.id}>
                              {item.Nama}
                            </option>
                            ))}
                          </select>
                        </div>

                        <div className="form-group">
                          <label>Tgl Keberangkatan</label>
                          <div className="form-group">
                            <div className="input-group">
                              <input type="date" className="form-control" 
                                value={tgl}
                                onChange={handleDateChange}
                                min={formatTodayDate()}
                                required
                              />
                            </div>
                          </div>
                        </div>
                      </div>
                      {/* /.col */}
                      <div className="col-md-6">
                        <div className="form-group">
                          <label>Bandara Tujuan</label>
                          <select
                            className="form-control select2bs4"
                            style={{ width: "100%" }}
                            value={keID}
                            onChange={(e) => setKeID(e.target.value)}
                            required
                          >
                            {data.map((item) => (
                            <option key={item.id} value={item.id}>
                              {item.Nama}
                            </option>
                            ))}
                          </select>
                        </div>

                        <div className="form-group">
                          {/* <label>Jumlah Penumpang</label>
                          <input
                            type="number"
                            class="form-control"
                            placeholder="Jumlah Penumpang"
                            value={penumpang}
                            onChange={(e) => setPenumpang(e.target.value)}
                            required
                          /> */}
                          <div className="passenger-input">
      <Dropdown>
        <Dropdown.Toggle variant="light" id="passenger-dropdown">
          Jumlah Penumpang
        </Dropdown.Toggle>
        <Dropdown.Menu>
          <Form.Group>
            <Form.Label>Dewasa (12 thn atau lebih)</Form.Label>
            <Form.Control
              type="number"
              value={passengers.dewasa}
              onChange={(e) => handlePassengerChange('dewasa', e.target.value)}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Anak (2 - 11 thn)</Form.Label>
            <Form.Control
              type="number"
              value={passengers.anak}
              onChange={(e) => handlePassengerChange('anak', e.target.value)}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Bayi (di bawah 2 tahun)</Form.Label>
            <Form.Control
              type="number"
              value={passengers.bayi}
              onChange={(e) => handlePassengerChange('bayi', e.target.value)}
            />
          </Form.Group>
        </Dropdown.Menu>
      </Dropdown>
    </div>

                        </div>
                      </div>
                    </div>
                    <p className="text-danger">{error}</p>
                  </div>

                </div>
                <div className="card-footer">
                    <button onClick={handleSubmit} type="submit" class="btn btn-primary float-right">
                      Cari Penerbangan
                    </button>
                </div>
                

                
              </div>
            </div>
            {/* /.container-fluid */}
          </section>
          {/* /.content */}
        </div>
      </div>
  );
};

export default Pesanan;
